/** 同步两个元素之间的 scrollLeft
 *  在 x.onScroll 中设置 y.scrollLeft 将会触发 y.onScroll (反之亦然)
 *  为了避免相互调用，在设置 scrollLeft 该方法会用 .skip 记录接下来需要忽略的 scroll 事件
 */
export declare function syncScrollLeft(x: HTMLDivElement, y: HTMLDivElement): import("rxjs").Subscription;
export declare function query<T extends Element = HTMLDivElement>(elem: HTMLDivElement, className: string): T;
export declare function queryAll<T extends Element = HTMLDivElement>(elem: HTMLDivElement, className: string): NodeListOf<T>;
